using RanseiLink.Core.Enums;
using RanseiLink.Core.Services.ModelServices;

namespace RanseiLink.Console.ModelCommands;

[Command("moveanimation", Description = "Get data on a given MoveAnimation.")]
public class MoveAnimationCommand(ICurrentModService currentModService) : ICommand
{
    [CommandParameter(0, Description = "MoveAnimation ID.", Name = "id")]
    public MoveAnimationId Id { get; set; }

    public ValueTask ExecuteAsync(IConsole console)
    {
        if (!currentModService.TryGetCurrentModServiceGetter(out var services))
        {
            console.WriteLine("No mod selected");
